// Multi-Agent Output Processor - Handle new brand-aligned format
const processMultiAgentOutput = function() {
  try {
    // Get the multi-agent output from the first input item
    const multiAgentOutput = $input.first().json.output || $input.first().json;

    let jsonContent = multiAgentOutput;

    // Remove markdown code block wrapper if present
    if (typeof jsonContent === 'string') {
      // Remove ```json and ``` markers
      jsonContent = jsonContent.replace(/```json\s*/g, '').replace(/```\s*$/g, '');

      // Find the first { and last } to extract just the JSON
      const firstBrace = jsonContent.indexOf('{');
      const lastBrace = jsonContent.lastIndexOf('}');

      if (firstBrace !== -1 && lastBrace !== -1) {
        jsonContent = jsonContent.substring(firstBrace, lastBrace + 1);
      }
    }

    console.log('DEBUG: Cleaned JSON content length:', jsonContent.length);
    console.log('DEBUG: JSON content preview:', jsonContent.substring(0, 200) + '...');

    // Parse the JSON content
    const parsedData = typeof jsonContent === 'string' ? JSON.parse(jsonContent) : jsonContent;

    console.log('DEBUG: Successfully parsed multi-agent output');
    console.log('DEBUG: Final tweets count:', parsedData.final_tweets?.length || 0);

    // Return the multi-agent output for downstream processing
    return [{
      json: parsedData
    }];

  } catch (error) {
    console.error('Error processing multi-agent output:', error);
    console.error('Input content preview:', JSON.stringify($input.first().json).substring(0, 500));

    return [{
      json: {
        error: true,
        message: error.message,
        type: "multi_agent_processing_error",
        debug_info: {
          input_type: typeof $input.first().json.output,
          input_length: $input.first().json.output?.length || 0,
          input_preview: $input.first().json.output?.substring(0, 200) || 'N/A'
        }
      }
    }];
  }
};

return processMultiAgentOutput();

// Transform Tweet Data - Process Flask API Response
// Extracts new_tweets from Flask duplicate-check response and formats for Batch Aggregator

const processFlaskResponse = function() {
  try {
    // Get the Flask API response from the first input item
    const flaskResponse = $input.first().json;

    // Handle both array format [{}] and direct object format {}
    let responseData;
    if (Array.isArray(flaskResponse) && flaskResponse.length > 0) {
      responseData = flaskResponse[0]; // Extract first object from array
    } else {
      responseData = flaskResponse;
    }

    // Extract new tweets from the Flask response
    const newTweets = responseData.data?.new_tweets || [];
    const executionId = responseData.data?.execution_id || 'unknown';
    const sourceUrl = responseData.data?.source_url || 'unknown';

    console.log(`DEBUG: Processing ${newTweets.length} new tweets from Flask API`);
    console.log(`DEBUG: Execution ID: ${executionId}`);
    console.log(`DEBUG: Duplicates found: ${responseData.summary?.duplicates_found || 0}`);

    // Transform each new tweet to the expected format for Batch Aggregator
    const transformedTweets = newTweets.map(tweet => ({
      json: {
        'Tweet ID': tweet['Tweet ID'],
        'URL': tweet.URL,
        'Content': tweet.Content,
        'Likes': tweet.Likes || 0,
        'Retweets': tweet.Retweets || 0,
        'Replies': tweet.Replies || 0,
        'Quotes': tweet.Quotes || 0,
        'Views': tweet.Views || 0,
        'Date': tweet.Date,
        'Tweet': tweet.Tweet,
        'Status': tweet.Status || 'success',
        // Additional metadata from Flask response
        'Execution_ID': executionId,
        'Source_URL': sourceUrl,
        'Processed_At': new Date().toISOString()
      }
    }));

    // Log processing results
    console.log(`DEBUG: Transformed ${transformedTweets.length} tweets for Batch Aggregator`);
    console.log(`DEBUG: Flask API Status: ${responseData.status}`);
    console.log(`DEBUG: Flask Message: ${responseData.message}`);

    return transformedTweets;

  } catch (error) {
    console.error('Error processing Flask API response:', error);

    // Return error object for debugging
    return [{
      json: {
        error: true,
        message: `Transform Tweet Data Error: ${error.message}`,
        type: "flask_response_processing_error",
        debug_info: {
          input_structure: Object.keys($input.first().json),
          input_sample: JSON.stringify($input.first().json).substring(0, 500)
        }
      }
    }];
  }
};

// Execute the processing and return results
return processFlaskResponse();

// Transform Multi-Agent Output to Flask API Format
const transformToFlaskFormat = function() {
  try {
    const multiAgentData = $input.first().json;
    const currentDate = new Date();
    const campaignBatch = 'batch_' + currentDate.toISOString().split('T')[0] + '_' + currentDate.toISOString().split('T')[1].substring(0, 5).replace(':', '-');

    // Transform final_tweets to expected Flask format
    const transformedTweets = multiAgentData.final_tweets.map(function(tweet, index) {
      // Map audience to type
      const audienceToType = {
        'Scientists': 'scientific_compute',
        'Data Centers': 'infrastructure_optimization', 
        'Technical Builders': 'developer_tools',
        'Agents': 'autonomous_systems'
      };

      // Generate coophive_elements based on audience and CTA
      const coophiveElements = [];
      if (tweet.audience === 'Scientists') coophiveElements.push('agent-first', 'compute-orchestration', 'queue-elimination');
      if (tweet.audience === 'Data Centers') coophiveElements.push('marketplace-composability', 'revenue-optimization', 'idle-capacity');
      if (tweet.audience === 'Technical Builders') coophiveElements.push('game-theoretic-primitives', 'marketplace-design', 'infra-abstraction');
      if (tweet.audience === 'Agents') coophiveElements.push('autonomous-deals', 'solver-networks', 'beyond-human-reach');

      // Generate discord_voice_patterns based on cyber_architect_score
      const voicePatterns = [];
      if (tweet.cyber_architect_score >= 9) voicePatterns.push('blueprint-precision', 'architectural-clarity');
      if (tweet.agent_first_score >= 9) voicePatterns.push('agent-centric', 'autonomous-orchestration');
      if (tweet.composability_score >= 8) voicePatterns.push('modular-design', 'composable-primitives');

      return {
        id: campaignBatch + '-tweet-' + (index + 1),
        type: audienceToType[tweet.audience] || 'brand_aligned',
        content: tweet.content,
        character_count: tweet.character_count,
        engagement_hook: tweet.cta,
        coophive_elements: coophiveElements,
        discord_voice_patterns: voicePatterns,
        theme_connection: multiAgentData.revision_summary,
        timestamp: currentDate.toISOString(),
        ready_for_deployment: tweet.character_count <= 280,
        campaign_batch: campaignBatch,
        status: 'Brand Aligned',
        is_edited: false
      };
    });

    // Create Flask-compatible payload
    const flaskPayload = {
      campaign_batch: campaignBatch,
      generated_at: currentDate.toISOString(),
      analysis_summary: {
        input_batch_size: transformedTweets.length,
        dominant_themes: ['brand_alignment', 'cyber_architect', 'agent_first'],
        top_engagement_score: Math.max(...multiAgentData.final_tweets.map(t => t.engagement_delta)),
        content_strategy: 'Multi-agent brand alignment with Cyber Architect voice',
        brand_alignment_score: multiAgentData.brand_alignment_analysis.batch_cyber_architect_avg
      },
      tweets: transformedTweets,
      tweet_count: transformedTweets.length,
      ready_for_deployment: transformedTweets.filter(t => t.ready_for_deployment).length,
      title: "Multi-Agent Brand-Aligned Content",
      description: "AI-generated tweets optimized for CoopHive brand voice and audience targeting",
      source_type: "multi_agent_automation"
    };

    return [{
      json: flaskPayload
    }];

  } catch (error) {
    console.error('Error transforming to Flask format:', error);
    return [{
      json: {
        error: true,
        message: error.message,
        type: "flask_transform_error"
      }
    }];
  }
};

return transformToFlaskFormat();

// Filter Latest 10 Tweets - Preserve Original Format
const filterLatestTweets = function() {
  try {
    // Get the input data from the previous node
    const inputData = $input.first().json;

    // Extract tweets array
    const allTweets = inputData.tweets || [];

    console.log(`DEBUG: Total tweets received: ${allTweets.length}`);

    // Sort tweets by date (newest first) and take latest 10
    const sortedTweets = allTweets.sort((a, b) => {
      const dateA = new Date(a.Date);
      const dateB = new Date(b.Date);
      return dateB - dateA; // Descending order (newest first)
    });

    const latest10Tweets = sortedTweets.slice(0, 3);

    console.log(`DEBUG: Filtered to latest ${latest10Tweets.length} tweets`);
    console.log(`DEBUG: Date range: ${latest10Tweets[latest10Tweets.length-1]?.Date} to ${latest10Tweets[0]?.Date}`);

    // Preserve the exact same structure as input
    const filteredOutput = {
      execution_id: inputData.execution_id,
      source_url: inputData.source_url,
      tweets: latest10Tweets,
      batch_metadata: {
        ...inputData.batch_metadata,
        total_tweets: latest10Tweets.length,
        original_total: allTweets.length,
        filtering_applied: true,
        filtering_timestamp: new Date().toISOString()
      }
    };

    return [{
      json: filteredOutput
    }];

  } catch (error) {
    console.error('Error filtering latest tweets:', error);
    return [{
      json: {
        error: true,
        message: error.message,
        type: "tweet_filtering_error",
        debug_info: {
          input_structure: Object.keys($input.first().json),
          input_tweet_count: $input.first().json.tweets?.length || 0
        }
      }
    }];
  }
};

return filterLatestTweets();

